let credits = { 
    "student_names": "Amelie Schroeder, Ayleen Weidmann", 
    "recode_title": "Hypertransformations", 
    "recode_year": 1975, 
    "recode_medium": "Benson plotter drawing on paper", 
    "recode_url": "https://spalterdigital.com/artists/vera-molnar/" 
} 

mySeed = 221

function setup() {
	createCanvas(windowWidth, windowHeight)
	buildGUI()
	angleMode(DEGREES)
}

function draw() {
	background(255);
	if(toggleColour.checked()) {
		background(0)
	}

	textAlign(CENTER);
	drawWords(width * 0.50);

	translate(width / 2, height / 2);

	randomSeed(sliderSeed.value())
	strokeWeight(sliderStroke.value())

	let squares = sliderSquares.value();
	let maxSize = 600;
	let step = maxSize / squares;
	let irregularity = 5;

	for(let i = squares; i > 0; i--) {
		rotate(sliderRotation.value())

		if(toggleSeed.checked()) {
			irregularity += round(random(20))
		} else {
			irregularity += .1
		}

		let size = i * step;
		let halfSize = size / 2;
		beginShape();
		vertex(-halfSize + random(-irregularity, irregularity), -halfSize + random(-irregularity, irregularity));
		vertex(halfSize + random(-irregularity, irregularity), -halfSize + random(-irregularity, irregularity));
		vertex(halfSize + random(-irregularity, irregularity), halfSize + random(-irregularity, irregularity));
		vertex(-halfSize + random(-irregularity, irregularity), halfSize + random(-irregularity, irregularity));
		endShape(CLOSE);

		if(toggleColour.checked()) {
			fill(random(255), random(255), random(255), random(255))
			//	background(0,0,0, 1)
		} else {
			noFill()
		}
	}
}

function resetGUI() {
	background(0)
	sliderSeed.value(221)
	sliderStroke.value(2)
	sliderRotation.value(90)
	sliderSquares.value(30)
	toggleSeed.checked(false)
	toggleColour.checked(false)
}

function colourGui() {}


function mousePressed() {
	mySeed = random(9999)
}

function buildGUI() {
	guiHolder = createDiv('').class('gui-holder')
	guiBar = createDiv('FUNKY BOX').parent(guiHolder).class('gui-bar').draggable(guiHolder)
	guiContent = createDiv('').parent(guiHolder).class('gui-content')

	guiItem = createDiv('').parent(guiContent)
	createDiv('SEED').parent(guiItem).class('gui-label')
	sliderSeed = createSlider(1, 9999, 221, 2).parent(guiItem).class('gui-slider')


	guiItem = createDiv('').parent(guiContent)
	createDiv('STROKE').parent(guiItem).class('gui-label')
	sliderStroke = createSlider(0.5, 15, 2, .01).parent(guiItem).class('gui-slider')

	guiItem = createDiv('').parent(guiContent)
	createDiv('ROTATION').parent(guiItem).class('gui-label')
	sliderRotation = createSlider(1, 360, 90, .01).parent(guiItem).class('gui-slider')

	guiItem = createDiv('').parent(guiContent)
	createDiv('SQUARES').parent(guiItem).class('gui-label')
	sliderSquares = createSlider(15, 300, 30, 1).parent(guiItem).class('gui-slider')

	createDiv('').parent(guiItem).class('gui-label')
	toggleSeed = createCheckbox('GO CRAZY!', false).parent(guiItem).class('gui-labelB')

	createDiv('').parent(guiItem).class('gui-label')
	toggleColour = createCheckbox('COLOUR', false).parent(guiItem).class('gui-labelB')



	guiItem = createDiv('').parent(guiContent).class('gui-item')
	createButton('RESET').parent(guiItem).mousePressed(resetGUI)


	createElement('style', `
		.gui-holder{
			position:fixed;
			right:25px;
			top:25px;
			background:rgba(255, 255, 255, .9);
			width:200px;
			color:#ff;
			border:2px solid;
			margin: 2px;
		}
		.gui-bar{
			color:#ff;
			padding: 2px;
			border-bottom:1px solid;
			font-family:avenir;
			font-size: 34px;

		}
		.gui-content{
			//background:blue;
			width:100%;
			height:100%;


		}
		.gui-slider{
            width:95%;
            -webkit-appearance: none;
            padding:2px;
		}
		.gui-slider::-webkit-slider-thumb{
			-webkit-appearance: none;
            border: 1.5px solid #000000;
            height: 15px;
            width: 15px;
            border-radius: 100%;
            background: #ffffff;
            box-shadow: 1px 1px 1px #000000, 0px 0px 1px #0d0d0d;



		}
		.gui-slider::-webkit-slider-runnable-track{
			width: 50%;
            cursor: pointer;
            background: #c9c9c9;
            border-radius: 1px;
            border: 1px solid #010101;


		}
		.gui-label{
			font-size: 13px;
			padding: 2px;
			font-family:avenir;


		}
		.gui-labelB{ //gocrazy
		border-top:1px solid;
		border-top:1px solid;
			font-family:avenir;
			font-size: 13px;


		}
		.gui-item{ 
		padding:5px;
		padding-top:8px;




		}
	`)
}

function drawWords(x) {
	push()
  fill(0);
  text('JOB FROM MOLNAR', x, 50);

  fill(0);
  text('15.06.04', x, 30);
  pop()
}
