let credits = {
	"student_names": "Ming Lu, Laurel Biolley",
	"recode_title": "Ricercar Etude II (To Search Out Study II)",
	"recode_year": 1985,
	"recode_medium": "Ink on Paper",
	"recode_url": "https://spalterdigital.com/artworks/ricecar-etude-ii/"
}




let img
let h1, h, i, j, h2, s2, b2;
let w = 25
let layerPaper

let bar = []

function setup() {
	createCanvas(windowWidth, windowHeight);
	buildGUI()
	layerPaper = createGraphics(width / 2, height / 2)
	angleMode(DEGREES)
	translate(width / 2, height / 2);
	background(210);
	// imageMode(CENTER);


	paperTexture2();

	genBar()

}

function genBar() {
	bar = []
	for(let x = 0; x <= width; x = x + 10 + random(40)) {
		bar.push({
			x: x + random(10),
			y: random(-20, 20),
			h: random(-10, 10),
			rot: random(-5, 5),
			edge: random(10),


			x2: x + random(20),
			y2: random(-20, 20),
			h2: random(-10, 10),
			rot2: random(-5, 5),
			edge2: random(10),

			x3: x + random(20),
			y3: random(-20, 20),
			h3: random(-10, 10),
			rot3: random(-5, 5),
			edge3: random(10),
		})
	}

}

function draw() {
	// background(220)
	background(210);
	image(layerPaper, 0, 0, width, height)
	noStroke()
	fill(0, 190 + 1 * sliderOpacity.value())
	rectMode(CENTER)




	for(var i = 0; i < bar.length; i++) {
		let b = bar[i]
		push()
		translate(b.x + random(0), 300 + +b.y * 1)

		var b1rot = 0
		if(toggleAnimate.checked()) {
			b1rot = frameCount + i * 1 * sliderAnimate.value()
		} else {
			b1rot = 0
		}
		rotate(b.rot * sliderRotation.value() + b1rot)
		rect(0, 0, 25 + w * sliderWidth.value(), 200 + b.h * 1 * sliderSize.value(), 2)
		rect(w / 2 - b.edge / 2, 0, b.edge, 200 + b.h * 1 * sliderSize.value(), 2)
		pop()
	}

	for(var i = 0; i < bar.length; i++) {
		let b = bar[i]
		push()
		translate(b.x2 + random(0), 460 + b.y2 * 1)
		var b2rot = 0
		if(toggleAnimate.checked()) {
			b2rot = frameCount + i * 1 * sliderAnimate.value()
		} else {
			b2rot = 0
		}
		rotate(b.rot2 * sliderRotation.value() + b2rot)
		rect(0, 0, 25 + w * sliderWidth.value(), 200 + b.h2 * 1 * sliderSize.value(), 2)
		rect(w / 2 - b.edge / 2, 0, b.edge2, 200 + b.h2 * 1 * sliderSize.value(), 2)
		pop()
	}

	for(var i = 0; i < bar.length; i++) {
		let b = bar[i]
		push()
		translate(b.x3 + random(0), 620 + b.y3 * 1)
		var b3rot = 0
		if(toggleAnimate.checked()) {
			b3rot = frameCount + i * 1 * sliderAnimate.value()
		} else {
			b3rot = 0
		}
		rotate(b.rot3 * sliderRotation.value() + b3rot)
		rect(0, 0, 25 + w * sliderWidth.value(), 200 + b.h3 * 1 * sliderSize.value(), 2)
		rect(w / 2 - b.edge / 2, 0, b.edge3, 200 + b.h3 * 1 * sliderSize.value(), 2)
		pop()
	}
}

function keyPressed() {
	if(key === ' ') {
		if(isLooping) {
			noLoop()
		} else {
			loop()
		}
		isLooping = !isLooping
	}


}

// function mousePressed() {
// 	genBar()
// }

function paperTexture2() {
	layerPaper.noFill();
	textureNum = layerPaper.width * layerPaper.height / 50;
	for(i = 0; i < textureNum; i++) {
		layerPaper.stroke(random(100, 150), random(100, 150), random(100, 150), 6);
		x = random(-layerPaper.width * 0.2, layerPaper.width * 1.2);
		y = random(-layerPaper.height * 0.2, layerPaper.height * 1.2);
		layerPaper.push();
		layerPaper.translate(x, y);
		layerPaper.strokeWeight(3);
		layerPaper.point(0, 0);
		layerPaper.strokeWeight(1);
		layerPaper.rotate(random(PI * 2));
		layerPaper.curve(random(60, 220), 0, 0, random(-50, 50), random(-50, 50), random(60, 120), random(60, 120), random(60, 220));
		layerPaper.pop();
	}
}



function resetGUI() {
	background(0)
	sliderRotation.value(0)
	sliderSize.value(0)
	sliderWidth.value(0)
	sliderAnimate.value(0)
	sliderWidth.value(0)
	sliderOpacity.value(0)
}

function randomGUI() {
	sliderRotation.value(random(-50, 50))
	sliderSize.value(random(-100, 100))
	sliderAnimate.value(random(-10, 10))
	sliderWidth.value(random(-5, 5))
	sliderOpacity.value(random(-150, 255))
}




function buildGUI() {



	guiHolder = createDiv('').class('gui-holder')
	guiBar = createDiv('').parent(guiHolder).class('gui-bar').draggable(guiHolder)

	mixDropdown = createSelect().parent(guiBar).class('dropDown');
	mixDropdown.option('MIX');
	mixDropdown.option('SLIDERS');
	mixDropdown.changed(updateSlidersVisibility);

	guiContent = createDiv('').parent(guiHolder).class('gui-content')

	guiItem = createDiv('').parent(guiContent).class('gui-item')
	createDiv('ROTATION').parent(guiItem).class('gui-label')
	sliderRotation = createSlider(-50, 50, 1, .01).parent(guiItem).class('gui-slider')
	createDiv('ANIMATE').parent(guiItem).class('gui-label')
	toggleAnimate = createCheckbox('', false).parent(guiItem).class('checkbox')
	//slidebar
	createDiv('SPEED').parent(guiItem).class('gui-label')
	sliderAnimate = createSlider(-10, 10, 1, .01).parent(guiItem).class('gui-slider')
	//	guiItem = createDiv('').parent(guiContent).class('gui-item')
	//checkbox


	guiItem = createDiv('').parent(guiContent).class('gui-item')
	createDiv('SIZE').parent(guiItem).class('gui-label')
	sliderSize = createSlider(-100, 100, 1, .01).parent(guiItem).class('gui-slider')

	guiItem = createDiv('').parent(guiContent).class('gui-item')
	createDiv('WIDTH').parent(guiItem).class('gui-label')
	sliderWidth = createSlider(-5, 5, 1, .01).parent(guiItem).class('gui-slider')

	guiItem = createDiv('').parent(guiContent).class('gui-item')
	createDiv('OPACITY').parent(guiItem).class('gui-label')
	sliderOpacity = createSlider(-150, 255, 3, .01).parent(guiItem).class('gui-slider')



	guiItem = createDiv('').parent(guiContent).class('Button')
	createButton('RANDOM').parent(guiItem).mousePressed(randomGUI).class('random-button')
	createButton('RESET').parent(guiItem).mousePressed(resetGUI).class('reset-button')


	createElement('style', `
		.gui-holder{
			position:fixed;
			right:25px;
			top:25px;
		//	background:rgba(0, 0, 0, .5);
			width:200px;
			height:318px;
			color:#fff;
		//	border:1px solid #fff ;
			font-family:monospace;

		}
		.gui-bar{
			color:#fff;
			//border-bottom:1px solid #fff;
			padding:5px;
				background:rgba(0, 0, 0, .5);
		}
		.gui-content{
			// background:blue;
			width:100%;
			height:100%;


		}
		.gui-slider{
			width:95%;
			b:rgba(0,0,0,0);
		}
		.gui-label{


		}
		.gui-item{
		//	border-bottom:1px solid #fff;
			// margin-bottom:15px;
			padding:5px;
				background:rgba(0, 0, 0, .5);
		}

		.Button{
			// border-bottom:1px solid #fff;
			// margin-bottom:15px;
			padding:5px;
				background:rgba(0, 0, 0, .5);

		}

		.reset-button{
		width:50%;
		background:rgba(0,0,0,7);
		color:#fff;
		}

			.random-button{
		width:50%;
		background:rgba(0,0,0,7);
		color:#fff;
		}
		.gui-item:hover{
			background:#333;

		}

		.dropDown{
		padding:0px;
		background:rgba(0,0,0,0);
