	`)
}


function updateSlidersVisibility() {
	let selectedOption = mixDropdown.value();
	if(selectedOption === 'MIX') {
		guiContent.style('display', 'none');
	} else if(selectedOption === 'SLIDERS') {
		guiContent.style('display', 'block');
	}
}
